import React, { Component } from 'react';
import {
  StyleSheet,
  Text,
  AppRegistry,
  View,
  Button,
  Image
} from 'react-native';
import { StackNavigator } from 'react-navigation';
import RNFetchBlob from 'react-native-fetch-blob';

var base64Icon = 'data:image/png;base64,';



export default class TriScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state={
      base64Pic:base64Icon,
      // truePath:base64Icon
    }
  }
  static navigationOptions = {
    title: 'Test 123',
  };
  componentDidMount(){
    this.LoadData();
  }
  LoadData() {
    RNFetchBlob
      .config({ 
        fileCache : true 
              })
      .fetch('GET', 'https://uniqueway.b0.upaiyun.com/uploads/2017/04/c529d27d-cc6d-432b-8ed6-cd26c3adaa55.jpg', {
      })
      // when response status code is 200
      .then((res) => {
              return res.readFile('base64')
      })
      .then((base64Str)=>{
        let base64Src = 'data:image/png;base64,'+ base64Str;
        this.setState({
          base64Pic: base64Src
        })
      })
      .catch((errorMessage, statusCode) => {
       console.log(errorMessage)
      })
  }
  render() {
    return (
      <View>
        <Image
          style={{
            marginTop:10,
            marginLeft:20,
            width:100,
            height:100
          }}
          source={{uri: this.state.base64Pic, scale: 1}}
        />
        {/* <Text>{this.state.truePath}</Text> */}
      </View>
    );
  }
}